// Function to find relevant products based on user message content
export function findRelevantProducts(message: string): HealthProduct[] {
  const lowerCaseMessage = message.toLowerCase();
  
  return healthProducts.filter(product => 
    product.keywords.some(keyword => lowerCaseMessage.includes(keyword))
  ).slice(0, 2); // Limit to max 2 products to avoid overwhelming the user
}
